import java.io.*;
import edu.duke.*;

public class Part3 {
    public void processGenes(StorageResource sr){
        int nineCharCount = 0, cgRatioCount = 0;
        float cgRatioValue =0;
        int lonDna = 0 ;

        for (String s : sr.data()){
            // System.out.println(s);

            if(s.length() > 60){
                nineCharCount  =nineCharCount + 1;
                // System.out.println(s);
            }
            cgRatioValue = cgRatio(s);
            if(cgRatioValue > 0.35){
                cgRatioCount = cgRatioCount + 1;
                // System.out.println("C-G-ratio is higher than 0.35" + s);
            }

            if(s.length()> lonDna){
                lonDna = s.length();
            }

        }
        System.out.println("number of Strings in sr that are longer than 60 characters" + nineCharCount);
        System.out.println("number of strings in sr whose C-G-ratio is higher than 0.35-->" + cgRatioCount);
        System.out.println("length of the longest gene in sr"+ lonDna);


    }

    public float cgRatio(String dna){
        int index =0, cgCount =0;
        while(index < dna.length()) {
            if (dna.substring(index, index+1).toUpperCase().equals("C") || dna.substring(index, index+1).toUpperCase().equals("G"))
            {
                cgCount = cgCount +1;
            }
            index ++;
        }
        if (cgCount == 0){
            return 0;
        }
        return (float)cgCount/dna.length();
    }



    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int currIndex;
        currIndex = dna.indexOf(stopCodon,startIndex+ stopCodon.length());
        while(currIndex != -1) {
            if ((currIndex -startIndex) % 3 == 0){
                return currIndex;
            }
            currIndex = dna.indexOf(stopCodon, currIndex + 1);

        }
        return dna.length();
    }

    public String findGene(String dna){
        int startCondon;
        // String 
        startCondon = dna.indexOf("ATG");
        if (startCondon == -1) {
            return "";
        }

        int taaIndex = findStopCodon(dna, startCondon, "TAA");
        int tagIndex = findStopCodon(dna, startCondon, "TAG");
        int tgaIndex = findStopCodon(dna, startCondon, "TGA");

        int minIndex = Math.min(taaIndex, Math.min(tagIndex,tgaIndex));
        if (dna.length() == minIndex){
            return "";
        }

        return dna.substring(startCondon, minIndex+3);
        
    }
    
    public StorageResource countGenes(String dna ){
        int countOcc = 0;
        int startIndex = 0;
        StorageResource sr = new StorageResource();
        String geneString = findGene(dna);
        sr.add(geneString);
        while(true){
            if(geneString.isEmpty()){
                break;
            }
            countOcc = countOcc +1 ;
            startIndex = dna.indexOf(geneString,startIndex) + geneString.length();
            geneString = findGene(dna.substring(startIndex));
            System.out.println(geneString);
            sr.add(geneString);
        }
        System.out.println("NO OF GENES --->" + countOcc);
        return sr;

    }



    public void testProcessGenes(){



        FileResource fr = new FileResource("brca1line.fa");
        String dna = fr.asString();

        dna = "";    
        dna = dna.toUpperCase();
        // System.out.println(dna);
        StorageResource countOcc = countGenes(dna);
        processGenes(countOcc);
        // System.out.println(countOcc);
        // float gene = cgRatio(dna);
        // System.out.println(gene);

    }


}
